import React from "react";
import classnames from "classnames";
import Heading from "../../1-atoms/Heading";
import ULListItem from "../UnorderedList";
import { ReactComponent as Check } from "../../1-atoms/Icons/Check.svg";

const checkBullet = (
  <div
    className={
      "bg-neonBlue rounded-full w-[16px] h-[16px] flex items-center justify-center"
    }
  >
    <Check />
  </div>
);

const itemsCH = [
  {
    icon: checkBullet,
    text: "reveal hidden spying apps",
    id: "check.1",
  },
  { icon: checkBullet, text: "stop silent location tracking", id: "check.2" },
  { icon: checkBullet, text: "avoid social media hacks", id: "check.3" },
  {
    icon: checkBullet,
    text: "provide 24/7 expert security help",
    id: "check.4",
  },
];

const VideoBlock: React.FC<React.AllHTMLAttributes<HTMLDivElement>> = ({
  ...props
}) => {
  return (
    <footer
      {...props}
      className={classnames("bg-ghostWhite px-[24px]", props.className)}
    >
      <video width="750" height="500" autoPlay loop>
        <source src={"/videos/Radar_2x.mp4"} type="video/mp4" />
      </video>
      <Heading level={2}>The anti-spy Clario app can:</Heading>
      <ULListItem items={itemsCH} />
    </footer>
  );
};

export default VideoBlock;
