import React, { useState } from "react";
import classnames from "classnames";
import Heading from "../Heading";
// import { ArrowM, ArrowS } from '../../1-atoms/Icons/24 ';

interface AccordionProps {
  title: string;
  accordionId: string;
  isExpanded: boolean;
  level?: number;
}

const Accordion: React.FC<
  AccordionProps & React.AllHTMLAttributes<HTMLDivElement>
> = ({
  title,
  accordionId,
  children,
  isExpanded = false,
  level = 3,
  ...props
}) => {
  const [open, toggleOpen] = useState(isExpanded);
  // const Heading = `h${level}` as keyof JSX.IntrinsicElements;

  return (
    <section {...props}>
      <details
        open={open}
        aria-expanded={open}
        id={`accordion-${accordionId}`}
        aria-controls={`sec-${accordionId}`}
        data-testid="accordion-button"
        onToggle={() => toggleOpen(!open)}
        aria-label={`accordion-${accordionId}`}
      >
        <>
          <summary
            className={
              "typography-2xl sm:typography-sm-bold flex justify-between text-left text-black100  outline-none focus-visible:border focus-visible:border-black000"
            }
          >
            <Heading
              // className="typography-lg leading-[1.6875rem] sm:text-base sm:leading-6"
              level={level}
            >
              {title}
            </Heading>
            {/*<ArrowM*/}
            {/*    className={classnames({ '-rotate-90': !open }, 'shrink-0 text-green100 sm:hidden')}*/}
            {/*/>*/}
            {/*<ArrowS*/}
            {/*    className={classnames({ '-rotate-90': !open }, 'shrink-0 text-green100 md:hidden')}*/}
            {/*/>*/}
          </summary>
        </>
        <p
          id={`sec-${accordionId}`}
          role="region"
          aria-labelledby={`accordion-${accordionId}`}
          // className={classnames(
          //   "transition-max-height typography-base overflow-auto pr-lg leading-5 text-black100 sm:mt-2xs sm:pr-0"
          // )}
        >
          {children}
        </p>
      </details>
    </section>
  );
};

export default Accordion;
