import React, { useState } from "react";
import classnames from "classnames";
import Heading from "../Heading";
import { ReactComponent as ArrowDown } from "../Icons/24px/ArrowDown.svg";

interface AccordionProps {
  title: string;
  accordionId: string;
  isExpanded?: boolean;
  level?: number;
  sequence: number;
}

const Accordion: React.FC<
  AccordionProps & React.AllHTMLAttributes<HTMLDivElement>
> = ({
  title,
  accordionId,
  children,
  isExpanded = false,
  level = 3,
  sequence,
  ...props
}) => {
  const [open, toggleOpen] = useState(isExpanded);

  return (
    <section
      {...props}
      className={classnames("flex w-full items-center", props.className)}
    >
      {/*<span*/}
      {/*  className={classnames(*/}
      {/*    { "bg-downy": open },*/}
      {/*    { "bg-luckyPoint": !open },*/}
      {/*    "w-[1.75rem] h-[1.75rem] rounded-full text-white font-bold mr-[0.5rem] text-center"*/}
      {/*  )}*/}
      {/*>*/}
      {/*  {sequence}*/}
      {/*</span>*/}
      <details
        open={open}
        aria-expanded={open}
        id={`accordion-${accordionId}`}
        aria-controls={`sec-${accordionId}`}
        onToggle={() => toggleOpen(!open)}
        aria-label={`accordion-${accordionId}`}
        className={"grow"}
      >
        <>
          <span
            className={classnames(
              { "bg-downy": open },
              { "bg-luckyPoint": !open },
              "w-[1.75rem] h-[1.75rem] rounded-full text-white font-bold mr-[0.5rem] text-center"
            )}
          >
            {sequence}
          </span>
          <summary className={"flex justify-between items-center"}>
            <Heading
              // className="typography-lg leading-[1.6875rem] sm:text-base sm:leading-6"
              className={classnames(
                { "text-downy": open },
                { "text-luckyPoint": !open },
                "mr-auto"
              )}
              level={level}
            >
              {title}
            </Heading>
            <ArrowDown
              className={classnames(
                { "-rotate-180 text-luckyPoint": !open },
                "shrink-0"
              )}
            />
          </summary>
        </>
        <p
          id={`sec-${accordionId}`}
          role="region"
          aria-labelledby={`accordion-${accordionId}`}
          // className={classnames(
          //   "transition-max-height typography-base overflow-auto pr-lg leading-5 text-black100 sm:mt-2xs sm:pr-0"
          // )}
        >
          {children}
        </p>
      </details>
    </section>
  );
};

export default Accordion;
