import React from "react";
import Flickity from "react-flickity-component";
import classnames from "classnames";
import Heading from "../../1-atoms/Heading";
import { ReactComponent as Arrow } from "../../1-atoms/Icons/24px/ArrowDown.svg";

interface CardCarouselProps {
  title: string;
  id: string;
  level?: number;
  items: JSX.Element[];
}

function CardCarousel({
  title,
  id,
  level = 2,
  items,
  className,
  ...props
}: CardCarouselProps & React.HTMLAttributes<HTMLDivElement>) {
  return (
    <section
      className={classnames("overflow-hidden py-10 mx-md md:mx-0", className)}
      {...props}
    >
      <Heading level={level} className={classnames("mb-5 text-center")}>
        {title}
      </Heading>
      <Flickity
        className={classnames("card-carousel py-5 space-x-[12px]", {})}
        options={{ groupCells: true, initialIndex: 1, arrowShape: <Arrow /> }}
      >
        {items.map((item, key) => (
          <div
            key={id + key}
            // className={
            //   "h-fit white-card ml-5 min-h-[12.5rem] w-full max-w-[30rem] shrink-0 snap-center p-5 last:mr-0 md:w-2/3	lg:w-1/3"
            // }
          >
            {item}
          </div>
        ))}
      </Flickity>
      {props.children}
    </section>
  );
}

export default CardCarousel;
