  //           },
  //           "border-none bg-neonBlue text-center text-white w-full rounded-xxl font-bold tracking-[-0.54px]",
  //           "justify-center",
  //           className
  //       )}
  //   >
  //     {children}
  //   </button>)}
  //
  // )</>
}

export default ButtonPrimary;
