import React from "react";
import "./styles/global.css";
import Accordion from "./UI/1-atoms/Accordion";

const Accord = [
  {
    title: "Install Clario",
    content: "Relax and let Clario do the job.",
    id: "Clario1",
  },
  {
    title: "Set up your protection",
    content: "Relax and let Clario do the job.",
    id: "Clario2",
  },
  {
    title: "Enjoy spy-free life",
    content: "Relax and let Clario do the job.",
    id: "Clario3",
  },
];

function App() {
  return (
    <div className={"max-w-[320px] mx-auto"}>
      <div>
        {Accord.map((el, ix) => {
          return (
            <React.Fragment key={el.id}>
              <Accordion
                sequence={ix + 1}
                accordionId={el.id}
                title={el.title}
                className={"w-full"}
              >
                {el.content}
              </Accordion>
              {ix !== Accord.length - 1 && (
                <span
                  className="ml-[14px] h-[40px] w-0 border-l-[1px] border-dashed border-blue-500 "
                  // style="left: 5.8%"
                />
              )}
            </React.Fragment>
          );
        })}
      </div>
    </div>
  );
}

export default App;
