import React, { useEffect, useState } from "react";
import { ReactComponent as Logo } from "../../1-atoms/Icons/ClarioLogo.svg";
import ButtonPrimary from "../../1-atoms/Buttons/Primary";
import classnames from "classnames";

export const Header = ({
  ...props
}: React.HTMLAttributes<HTMLHeadingElement>) => {
  return (
    <header
      {...props}
      className={classnames(
        "py-[12px] flex items-center justify-center h-[56px] sticky top-[0px] z-10 bg-white",
        props.className
      )}
    >
      <Logo className={"shrink-0"} />
    </header>
  );
};
export const HeadingWithButton = ({
  ...props
}: React.HTMLAttributes<HTMLHeadingElement>) => {
  const headerRef = React.useRef<HTMLDivElement>(null);
  const [headerIsStuck, setHeaderIsStuck] = useState(false);

  useEffect(() => {
    if (!headerRef.current) {
      return;
    }
    const stickHeader = () => {
      setHeaderIsStuck(
        headerRef?.current.getBoundingClientRect().top <= 16 &&
          headerRef?.current.getBoundingClientRect().bottom > 0
      );
    };
    document.addEventListener("scroll", stickHeader);
    return () => {
      document.removeEventListener("scroll", stickHeader);
    };
  }, [headerRef.current]);

  return (
    <header
      ref={headerRef}
      {...props}
      className={classnames(
        "py-[12px] flex items-center justify-center justify-between px-[24px] h-[56px] sticky top-[0px] z-9 bg-white",
        props.className
      )}
    >
      <Logo />
      <ButtonPrimary anchor size={"s"} className={"w-[127px]"}>
        Protect me now
      </ButtonPrimary>
    </header>
  );
};
