import classnames from "classnames";
import React from "react";

interface ButtonPrimaryProps {
  size?: "l" | "s";
}

function ButtonPrimary({
  size = "s",
  className,
  ...props
}: ButtonPrimaryProps & React.ButtonHTMLAttributes<HTMLButtonElement>) {
  return (
    <button
      className={classnames(
        { "pt-[9px] pb-[10px]": size === "l" },
        { "pt-[6px] pb-[8px]": size === "s" },
        "border-none bg-neonBlue text-center text-white  w-full rounded-xxl font-bold text-[18px] leading-[-0.54px]",
        "justify-center",
        className
      )}
      {...props}
    />
  );
}

export default ButtonPrimary;

